import React, { CSSProperties } from 'react';

type IconName =
  | 'home'
  | 'open-link'
  | 'typewriter'
  | 'left-arrow'
  | 'right-arrow';
type SpecialIconName = 'blog' | 'github' | 'linkedin';

export interface IconProps extends SvgProps {
  name: IconName | SpecialIconName;
}

export const Icon: React.FC<IconProps> = ({ name, ...svgProps }) => {
  switch (name) {
    case 'home':
      return <HomeIcon {...svgProps} />;
    case 'open-link':
      return <OpenInNewTabIcon {...svgProps} />;
    case 'typewriter':
      return <TypewriterIcon {...svgProps} />;
    case 'left-arrow':
      return <LeftArrowIcon {...svgProps} />;
    case 'right-arrow':
      return <RightArrowIcon {...svgProps} />;
    case 'blog':
      return <BlogIcon {...svgProps} />;
    case 'github':
      return <GitHubIcon {...svgProps} />;
    case 'linkedin':
      return <LinkedInIcon {...svgProps} />;
    default:
      return null;
  }
};

interface SvgProps {
  className?: string;
  style?: CSSProperties;
}

